mapper.select:
SELECT g.good_id, g.name, g.price, g.status, g.description, g.sales, g.remainder, g.create_time, g.category_id

FROM good g

WHERE g.good_id = #{goodId}

SELECT g.good_id, g.name, g.price, g.status, g.description, /*g.shop_id,*/ g.sales, g.remainder, g.category_id, g.create_time/*,
s.name as "shop.name"*/
FROM good g
/*INNER JOIN shop s ON g.shop_id = s.shop_id*/

    g.name LIKE '%' #{good.name} '%'
    AND g.category_id = #{good.categoryId}

order by create_time DESC
limit #{offset}, #{limit}

SELECT COUNT(*)
FROM good g
/*INNER JOIN shop s ON g.shop_id = s.shop_id*/

    g.name LIKE '%' #{good.name} '%'
    AND g.category_id = #{good.categoryId}

mapper.update:
UPDATE good
name = #{name}, price = #{price}, status = #{status}, description = #{description}, remainder = #{remainder}, category_id = #{categoryId}
WHERE good_id = #{goodId}
