mapper.update:
UPDATE
    order_form
SET
    status = #{status}
WHERE
    order_id = #{orderId}

mapper.select:
SELECT
    order_id, user_id, create_time, status, total_price, address, receiver, phone
FROM
    order_form

    user_id = #{order.userId}
    AND status = #{order.status}

order by create_time DESC
    limit #{offset}, #{limit}

SELECT
    COUNT(*)
FROM
    order_form

    user_id = #{order.userId}
    AND status = #{order.status}
