mapper.select:
SELECT user_id, username, password, phone, email, real_name, avatar, description, create_time
FROM user
WHERE user_id = #{userId}

SELECT user_id, username, password, phone, email, real_name, avatar, description, create_time
FROM user
WHERE username = #{userName}

mapper.update:
update user
password = #{password}, phone = #{phone}, email = #{email}, real_name = #{realName}, description = #{description}
WHERE user_id = #{userId}
