mapper.select:
SELECT user_id, username, password, phone, email, real_name, avatar, description, create_time
FROM user
WHERE user_id = #{userId}

SELECT user_id, username, password, phone, email, real_name, avatar, description, create_time
FROM user
WHERE username = #{userName}

SELECT user_id, username, password, phone, email, real_name, avatar, description, create_time
FROM user

    username LIKE '%' #{user.username} '%'
    AND phone = #{user.phone}
    AND email = #{user.email}
    AND real_name = #{user.realName}

order by create_time DESC
limit #{offset}, #{limit}

SELECT COUNT(*)
FROM user

    username LIKE '%' #{user.username} '%'
    AND phone = #{user.phone}
    AND email = #{user.email}
    AND real_name = #{user.realName}

mapper.update:
update user
password = #{password}, phone = #{phone}, email = #{email}, real_name = #{realName}, description = #{description}, avatar = #{avatar}
WHERE user_id = #{userId}
